package encryptdecrypt;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.FileWriter;
import java.io.IOException;
import java.util.Scanner;

class Main {
    private Encrypt encryptAlgorithm;
    private Decrypt decryptAlgorithm;


    public void setEncrypt(Encrypt encrypt) {
        this.encryptAlgorithm = encrypt;
    }

    public void setDecrypt(Decrypt decrypt) {
        this.decryptAlgorithm = decrypt;
    }

    public String algorithm(String mode, String text, int key){
        String result = "";
        if (mode.equals("enc")){
            result = encryptAlgorithm.encrypt(text, key);
        }else {
            result = decryptAlgorithm.decrypt(text, key);
        }
        return result;
    }

    public static void main(String[] args) {
        String mode = "enc";
        String alg = "shift";
        String data = "";
        int key = 0;
        String in = "";
        String out = "";
        String text = "";
        String result = "";
        Main cipher = new Main();
        Encrypt encShift = new EncryptShift();
        Decrypt decShift = new DecryprtShift();
        Encrypt encUnicode = new EncryptUnicode();
        Decrypt decUnicode = new DecryptUnicod();

        for (int i = 0; i < args.length; i += 2) {
            switch (args[i]){
                case "-mode":
                    mode = args[i + 1];
                    break;
                case "-key":
                    key = Integer.parseInt(args[i + 1]);
                    break;
                case "-data":
                    data = args[i + 1];
                    break;
                case "-in":
                    in = args[i + 1];
                    break;
                case "-out":
                    out = args[i + 1];
                    break;
                case "-alg":
                    alg = args[i + 1];
                    break;
                default:
                    break;
            }
        }

        if (!data.isEmpty()){
            text = data;
        }else if (!in.isEmpty()){
            text = cipher.readFromFile(in);
        }

        if (!mode.isEmpty()){
            if (alg.equals("shift")){
                cipher.setEncrypt(encShift);
                cipher.setDecrypt(decShift);
            }else {
                cipher.setEncrypt(encUnicode);
                cipher.setDecrypt(decUnicode);
            }
            result = cipher.algorithm(mode, text, key);
        }

        if (out.isEmpty()) {
            System.out.println(result);
        }else {
            cipher.printToFile(result, out);
        }

    }

    private void printToFile(String result, String out) {
        File file = new File(out);
        try(FileWriter fileWriter = new FileWriter(file)){
            fileWriter.write(result);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private String readFromFile(String in) {
        File file = new File(in);
        String text = "";

        try(Scanner scanner = new Scanner(file)) {
            text = scanner.nextLine();
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        }

        return text;
    }
}

interface Decrypt {
    String decrypt(String text, int key);
}


interface Encrypt {
    String encrypt(String text, int key);
}

class DecryprtShift implements Decrypt {
    @Override
    public String decrypt(String text, int key) {
        StringBuilder result = new StringBuilder();
        char ch;

        for (int i = 0; i < text.length(); i++) {
            if (Character.isLetter(text.charAt(i))){
                if (Character.isUpperCase(text.charAt(i))){
                    ch = (char) (((int) text.charAt(i) + 26 - key - 65) % 26 + 65);
                    result.append(ch);
                }else {
                    ch = (char) (((int) text.charAt(i) + 26 - key - 97) % 26 + 97);
                    result.append(ch);
                }
            }else {
                ch = text.charAt(i);
                result.append(ch);
            }
        }
        return  result.toString();
    }
}

class EncryptShift implements Encrypt {
    @Override
    public String encrypt(String text, int key) {
        StringBuilder result = new StringBuilder();
        char ch;

        for (int i = 0; i < text.length(); i++) {
            if (Character.isLetter(text.charAt(i))){
                if (Character.isUpperCase(text.charAt(i))){
                    ch = (char) (((int) text.charAt(i) + key - 65) % 26 + 65);
                    result.append(ch);
                }else {
                    ch = (char) (((int) text.charAt(i) + key - 97) % 26 + 97);
                    result.append(ch);
                }
            }else {
                ch = text.charAt(i);
                result.append(ch);
            }
        }
        return  result.toString();
    }
}

class DecryptUnicod implements Decrypt {
    @Override
    public String decrypt(String text, int key) {
        StringBuilder result = new StringBuilder();

        for (int i = 0; i < text.length(); i++) {
            int symbol = text.charAt(i) - key;
            result.append((char) symbol);
        }
        return result.toString();
    }
}

class EncryptUnicode implements Encrypt {

    @Override
    public String encrypt(String text, int key) {
        StringBuilder result = new StringBuilder();

        for (int i = 0; i < text.length(); i++) {
            int symbol = text.charAt(i) + key;
            result.append((char) symbol);
        }
        return result.toString();
    }
}
